// Module declarations for TypeScript
declare module '*.jsx' {
  const Component: any;
  export default Component;
}

declare module '*.json' {
  const value: any;
  export default value;
}

declare module '*.png' {
  const content: string;
  export default content;
}

declare module '*.jpg' {
  const content: string;
  export default content;
}

declare module '*.jpeg' {
  const content: string;
  export default content;
}

declare module '*.gif' {
  const content: string;
  export default content;
}

declare module '*.svg' {
  const content: string;
  export default content;
}